{
    "schemaVersion": "1.0.0",
    "class": "Device",
    "async": true,
    "label": "my BIG-IP declaration for declarative onboarding",
    "Common": {
        "class": "Tenant",
        "hostname": "{{bigip1_hostname}}",
        "myDns": {
            "class": "DNS",
            "nameServers": [
                "{{dns_server}}",
                "2001:4860:4860::8844"
            ],
            "search": [
                "f5lab.dev"
            ]
        },
        "myNtp": {
            "class": "NTP",
            "servers": [
                "0.pool.ntp.org",
                "1.pool.ntp.org",
                "2.pool.ntp.org"
            ],
            "timezone": "UTC"
        },
        "myProvisioning": {
            "class": "Provision",
            "ltm": "nominal",
            "avr": "nominal"
        },
        "traffic": {
            "class": "VLAN",
            "tag": 4094,
            "mtu": 1500,
            "interfaces": [
                {
                    "name": "1.1",
                    "tagged": false
                }
            ],
            "cmpHash": "src-ip"
        },
        "traffic-self": {
            "class": "SelfIp",
            "address": "{{bigip1_traffic_self_ip}}",
            "vlan": "traffic",
            "allowService": "all",
            "trafficGroup": "traffic-group-local-only"
        },
        "Default": {
            "class": "Route",
            "gw": "{{bigip1_default_route}}",
            "localOnly": true,
            "network": "default",
            "mtu": 1500
        },
        "configsync": {
            "class": "ConfigSync",
            "configsyncIp": "/Common/traffic-self/address"
        },
        "failoverAddress": {
            "class": "FailoverUnicast",
            "address": "/Common/traffic-self/address"
        },
        "dbvars": {
            "class": "DbVariables",
            "ui.advisory.enabled": true,
            "ui.advisory.color": "green",
            "ui.advisory.text": "/Common/hostname"
        }
    }
}

